Uri newUri = new Uri("https://some.random.url/random");\nSystem.Net.HttpWebRequest wr = (System.Net.HttpWebRequest)WebRequest.Create(newUri);\nwr.Method = "POST";\nwr.ContentType = "application/x-www-form-urlencoded";\n\n// If using a proxy, uncomment and set the following lines:\n// IWebProxy proxy = WebRequest.DefaultWebProxy;\n// proxy.Credentials = CredentialCache.DefaultCredentials; // or specify your credentials\n// wr.Proxy = proxy;\n\n// If not using a proxy, explicitly disable it:\nwr.Proxy = null;\n\nASCIIEncoding encoding = new ASCIIEncoding();\nbyte[] requestBytes = encoding.GetBytes("test");\nwr.ContentLength = requestBytes.Length;\nSystem.IO.Stream stream = wr.GetRequestStream(); // Now should not fail with ServerProtocolViolation\nstream.Write(requestBytes, 0, requestBytes.Length);\nstream.Close();\n\nHttpWebResponse response = (HttpWebResponse)wr.GetResponse();\nusing (StreamReader reader = new StreamReader(response.GetResponseStream()))\n{\n    string responseText = reader.ReadToEnd();\n    Console.WriteLine(responseText);\n}