using System;\nusing System.Data;\nusing System.Xml;\n\nclass Program\n{\n    static void Main()\n    {\n        // Assuming 'ds' is your DataSet and 'strXmlTestCasePath' is the path to write XML.\n        string strXmlTestCasePath = "path_to_your_file.xml";\n        DataSet ds = new DataSet(); // Your existing dataset\n\n        // Write the dataset to an XML file ignoring the schema\n        ds.WriteXml(strXmlTestCasePath, XmlWriteMode.IgnoreSchema);\n\n        // Load the written XML into an XmlDocument for further manipulation\n        XmlDocument xmlDoc = new XmlDocument();\n        xmlDoc.Load(strXmlTestCasePath);\n\n        // Create a new element or modify existing to add extra information\n        XmlElement extraInfo = xmlDoc.CreateElement("ExtraInformation");\n        extraInfo.InnerText = "Your additional information here";\n\n        // Assuming you want to add this information as the root element's child\n        xmlDoc.DocumentElement.AppendChild(extraInfo);\n\n        // Save the modified XML back to the file or another location\n        xmlDoc.Save(strXmlTestCasePath);\n    }\n}