using System;\nusing System.Data;\n\npublic class Program\n{\n    public static void Main()\n    {\n        // Assuming you have a DataSet named 'dataSet' filled with data from your SELECT statements.\n        DataTable dataTable = dataSet.Tables[0]; // or whichever table contains the ID\n\n        // Set the primary key column.\n        DataColumn[] keys = new DataColumn[1];\n        keys[0] = dataTable.Columns["ID"]; // Ensure "ID" is the name of your column\n        dataTable.PrimaryKey = keys;\n\n        // Now you can use DataRow.Find method, e.g. to find a row with ID=5\n        DataRow foundRow = dataTable.Rows.Find(5);\n\n        if (foundRow != null)\n        {\n            Console.WriteLine("Found Row: " + foundRow["Names"].ToString());\n        }\n        else\n        {\n            Console.WriteLine("No row found.");\n        }\n    }\n}