// C# example\nusing System;\n\nclass Hello {\n    static void Main() {\n        GC.Collect(); // request garbage collection for cleaner state\n        long startMemory = GC.GetTotalMemory(true);\n        Console.WriteLine("Hello World");\n        long endMemory = GC.GetTotalMemory(false);\n        Console.WriteLine($"Approximate memory used: {(endMemory - startMemory) / 1024} KB");\n    }\n}