lock (locker)\n{\n    // Double-check if the item still exists in cache after acquiring the lock\n    if (HttpRuntime.Cache[cacheKey] == null)\n    {\n        var dataToCache = GetSomeDataToCache();\n        HttpRuntime.Cache.Insert(cacheKey, dataToCache, null, DateTime.Today.AddDays(1), Cache.NoSlidingExpiration);\n        return dataToCache.Copy(); // Return the freshly loaded data\n    }\n    else\n    {\n        // Cache is populated; return a copy of the cached data\n        return ((SomeData)HttpRuntime.Cache[cacheKey]).Copy();\n    }\n}