// Assuming we have two tables: Employee and EmployeeDetails\npublic class Employee\n{\n    public int Id { get; set; }\n    public string Name { get; set; }\n\n    // One-to-One relationship with EmployeeDetails\n    public virtual EmployeeDetail EmployeeDetail { get; set; }\n}\n\npublic class EmployeeDetail\n{\n    public int DetailId { get; set; }\n    public int EmployeeId { get; set; } // Foreign key back to Employee\n    public string AdditionalInfo { get; set; }\n\n    // One-to-One relationship with Employee\n    public virtual Employee Employee { get; set; }\n}\n\n// DataContext setup would include:\npublic class MyDataContext : DataContext\n{\n    public Table<Employee> Employees;\n    public Table<EmployeeDetail> EmployeeDetails;\n\n    // Constructor and other configurations...\n}