using System;\nusing System.Data;\n\nclass Program\n{\n    static void Main()\n    {\n        // Assuming a DataTable named "dt" has already been populated with data.\n        DataTable dt = new DataTable();\n        dt.Columns.Add("EventDate", typeof(DateTime));\n        \n        DataRow row1 = dt.NewRow();\n        row1["EventDate"] = new DateTime(2023, 10, 5, 14, 30, 0);\n        dt.Rows.Add(row1);\n\n        DataRow row2 = dt.NewRow();\n        row2["EventDate"] = new DateTime(2023, 11, 15, 9, 45, 0);\n        dt.Rows.Add(row2);\n\n        // Create a DataSet and add the DataTable to it\n        DataSet ds = new DataSet();\n        ds.Tables.Add(dt);\n\n        // Define the date format string and the target date.\n        string targetDate = "10/05/2023 14:30:00";\n        string filterExpression = $"EventDate = '{targetDate}'";\n\n        try\n        {\n            DataRow[] selectedRows = ds.Tables[0].Select(filterExpression);\n            foreach (DataRow row in selectedRows)\n            {\n                Console.WriteLine(row["EventDate"]);\n            }\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine($"Error: {ex.Message}");\n        }\n    }\n}