using System;\nusing System.Management;\n\nforeach (ManagementObject dnsZone in mgrZones)\n{\n    // Get method parameters\n    ManagementBaseObject mngrZones = dnsZone.GetMethodParameters("ChangeZoneType");\n\n    // Define ZoneType: 1 - Primary, 2 - Stub, 3 - Secondary\n    mngrZones["ZoneType"] = Convert.ToUInt32(1); // Change to primary\n\n    // Specify whether the zone is integrated with AD DNS:\n    mngrZones["DsIntegrated"] = true; // Set to true if using Active Directory Integrated DNS\n\n    // File path for storing the zone data (required for Primary zones)\n    mngrZones["FilePath"] = @"%SystemRoot%\system32\dns\YourZoneFile.dns";\n\n    // Invoke the ChangeZoneType method with parameters\n    dnsZone.InvokeMethod("ChangeZoneType", mngrZones, null);\n}