// Example of generating Sequential GUID in C#\nusing System;\n\npublic class GuidExample {\n    public static void Main() {\n        Guid sequentialGuid = Guid.NewGuid();\n        byte[] guidBytes = sequentialGuid.ToByteArray();\n        \n        // Manipulate bytes to make it sequential (e.g., swap time-related parts)\n        SwapByte(guidBytes, 0, 3);    // Swap first three bytes\n        SwapByte(guidBytes, 1, 2);\n        SwapByte(guidBytes, 4, 5);    // Swap next two bytes\n        \n        Guid newSequentialGuid = new Guid(guidBytes);\n        Console.WriteLine("Standard GUID: " + sequentialGuid.ToString());\n        Console.WriteLine("Sequential GUID: " + newSequentialGuid.ToString());\n    }\n\n    private static void SwapByte(byte[] guidBytes, int posA, int posB) {\n        byte temp = guidBytes[posA];\n        guidBytes[posA] = guidBytes[posB];\n        guidBytes[posB] = temp;\n    }\n}