public static string CompressObjectToString(object obj)\n{\n    var json = JsonConvert.SerializeObject(obj); // Convert object to JSON string\n    byte[] bytes = Encoding.UTF8.GetBytes(json);\n    \n    using (var msi = new MemoryStream(bytes))\n    {\n        using (var memoryOutputStream = new MemoryStream())\n        {\n            using (var gzipStream = new GZipStream(memoryOutputStream, CompressionLevel.HighestCompression))\n            {\n                msi.CopyTo(gzipStream);\n                gzipStream.Close();\n                \n                var compressedData = memoryOutputStream.ToArray();\n                return Convert.ToBase64String(compressedData); // Encode bytes to Base64 string\n            }\n        }\n    }\n}\n\npublic static T DecompressStringToObject<T>(string compressed)\n{\n    byte[] gzipBytes = Convert.FromBase64String(compressed);\n    \n    using (var ms = new MemoryStream(gzipBytes))\n    {\n        using (var mso = new MemoryStream())\n        {\n            using (var gzipStream = new GZipStream(ms, CompressionMode.Decompress))\n            {\n                gzipStream.CopyTo(mso);\n\n                string json = Encoding.UTF8.GetString(mso.ToArray());\n                return JsonConvert.DeserializeObject<T>(json); // Convert JSON to object\n            }\n        }\n    }\n}