public void QueueReportRequest(int userId, DateTime startDate, DateTime endDate)\n{\n    // Implement logic to queue request\n    using (SqlConnection conn = new SqlConnection(connectionString))\n    {\n        string query = "INSERT INTO ReportRequests (UserId, StartDate, EndDate, Status) VALUES (@userId, @startDate, @endDate, 'Processing')";\n        using (SqlCommand cmd = new SqlCommand(query, conn))\n        {\n            cmd.Parameters.AddWithValue("@userId", userId);\n            cmd.Parameters.AddWithValue("@startDate", startDate);\n            cmd.Parameters.AddWithValue("@endDate", endDate);\n            conn.Open();\n            cmd.ExecuteNonQuery();\n        }\n    }\n}