using System;\nusing System.Windows.Forms;\n\npublic class CustomerForm : Form\n{\n    private ComboBox typeComboBox;\n    private BindingSource customerBindingSource = new BindingSource();\n\n    public CustomerForm()\n    {\n        // Assuming typesDataTable is already populated from your database\n        DataTable typesDataTable = GetTypesDataTable();\n\n        InitializeComponent();\n        \n        // Add a dummy item for null selection\n        DataRow newRow = typesDataTable.NewRow();\n        newRow["TypeID"] = DBNull.Value; \n        newRow["TypeName"] = "None"; \n        typesDataTable.Rows.InsertAt(newRow, 0); // Insert at the beginning\n\n        typeComboBox.DataSource = typesDataTable;\n        typeComboBox.DisplayMember = "TypeName";\n        typeComboBox.ValueMember = "TypeID";\n\n        customerBindingSource.DataSource = typeof(Customer);\n        typeComboBox.DataBindings.Add("SelectedValue", customerBindingSource, "Type");\n\n        // Load a sample customer object\n        LoadCustomer(new Customer { Type = (int?)null });\n    }\n\n    private void LoadCustomer(Customer customer)\n    {\n        customerBindingSource.DataSource = customer;\n    }\n\n    private DataTable GetTypesDataTable()\n    {\n        DataTable dt = new DataTable();\n        dt.Columns.Add("TypeID", typeof(int));\n        dt.Columns.Add("TypeName", typeof(string));\n\n        // Simulate loading data from database\n        dt.Rows.Add(1, "Type 1");\n        dt.Rows.Add(2, "Type 2");\n\n        return dt;\n    }\n}\n\npublic class Customer\n{\n    public int? Type { get; set; }\n}