public static class SessionHelpers\n{\n    private const string ACCOUNT_KEY = "CURRENT_ACCOUNT";\n\n    public static Account GetCurrentAccount(SessionStateBase session)\n    {\n        var accountObj = session[ACCOUNT_KEY];\n        if (accountObj != null && accountObj is Account)\n        {\n            return (Account)accountObj;\n        }\n        return null; // or handle the case when no account is found\n    }\n\n    public static void SetCurrentAccount(Account obj, SessionStateBase session)\n    {\n        if (obj != null)\n        {\n            session[ACCOUNT_KEY] = obj;\n        }\n        else\n        {\n            RemoveCurrentAccount(session);\n        }\n    }\n\n    public static void RemoveCurrentAccount(SessionStateBase session)\n    {\n        session.Remove(ACCOUNT_KEY);\n    }\n}