using Net.Pkcs11Interop.Common;\nusing Net.Pkcs11Interop.HighLevelAPI;\nusing System;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        // Define DLL path of your Thales WebSentry PKCS#11 module\n        string libraryPath = @"C:\path\to\thales\web_sentry_pkcs11.dll";\n        \n        using (Pkcs11 pkcs11 = new Pkcs11(libraryPath, AppType.MultiThreaded))\n        {\n            // Initialize the library\n            pkcs11.Initialize(null);\n\n            try\n            {\n                // Get available slots (hardware tokens)\n                Slot[] slots = pkcs11.GetSlotList(SlotFlag.TokenPresent);\n                \n                // Assuming first slot is the one we want to use\n                Slot slot = slots[0];\n\n                // Open a session to the slot\n                using (Session session = slot.OpenSession(SessionType.ReadWrite))\n                {\n                    // Login to the token\n                    session.Login(CKU.CKU_USER, "your-pin");\n\n                    // Perform cryptographic operations here\n\n                    // Logout before closing the session\n                    session.Logout();\n                }\n            }\n            catch (Exception ex)\n            {\n                Console.WriteLine($"An error occurred: {ex.Message}");\n            }\n            finally\n            {\n                pkcs11.Destroy();\n            }\n        }\n    }\n}