using System.Data;\nusing System.Data.SqlClient;\n\n// Connection string setup\nstring connectionString = CONNECTION_STRING;\n\n// Create a SqlDataAdapter using SqlCommand to execute the stored procedure\nusing (SqlConnection conn = new SqlConnection(connectionString))\n{\n    SqlDataAdapter adapter = new SqlDataAdapter("sp_mult_tables", conn);\n    adapter.SelectCommand.CommandType = CommandType.StoredProcedure;\n\n    // Create a DataSet to hold the results\n    DataSet dataSet = new DataSet();\n\n    // Fill the DataSet with data from the first result set of the query.\n    adapter.Fill(dataSet);\n\n    // Now 'dataSet' contains all the tables returned by your stored procedure.\n    // You can access them like:\n    DataTable table1 = dataSet.Tables[0]; // First table\n    DataTable table2 = dataSet.Tables[1]; // Second table\n\n    // Process your data tables as needed\n}