using Microsoft.Win32;\nusing System;\n\nnamespace NameHere\n{\n    class Program\n    {\n        static void Main(string[] args)\n        {\n            string baseKeyPath = @"SYSTEM\CurrentControlSet\Control\Print\Environments\Windows NT x86";\n            string strKey = "Test123";\n            string fullPath = baseKeyPath + "\\" + strKey;\n\n            try\n            {\n                deleteRegKey(fullPath);\n            }\n            catch (Exception ex)\n            {\n                Console.WriteLine("An error occurred: " + ex.Message);\n            }\n        }\n\n        static void deleteRegKey(string keyName)\n        {\n            using (RegistryKey hklm = Registry.LocalMachine.OpenSubKey(@"SYSTEM\CurrentControlSet\Control\Print\Environments", true))\n            {\n                if (hklm != null && hklm.GetSubKeyNames().Contains(keyName.Split('\\').Last()))\n                {\n                    hklm.DeleteSubKeyTree(keyName.Split('\\').Last(), false);\n                    Console.WriteLine("Registry key deleted successfully.");\n                }\n                else\n                {\n                    Console.WriteLine("Registry key not found.");\n                }\n            }\n        }\n    }\n}