using System;\nusing System.Data.SqlServerCe;\n\npublic class SqlCeBulkInsertExample\n{\n    private static void InsertData(SqlCeConnection connection, DataTable dataTable)\n    {\n        using (SqlCeCommand command = new SqlCeCommand("INSERT INTO YourTable (Column1, Column2) VALUES (@Value1, @Value2)", connection))\n        {\n            command.Parameters.Add("@Value1", System.Data.DbType.String);\n            command.Parameters.Add("@Value2", System.Data.DbType.Int32);\n\n            using (SqlCeTransaction transaction = connection.BeginTransaction())\n            {\n                try\n                {\n                    foreach (DataRow row in dataTable.Rows)\n                    {\n                        command.Parameters["@Value1"].Value = row["Column1"];\n                        command.Parameters["@Value2"].Value = row["Column2"];\n                        command.ExecuteNonQuery();\n                    }\n\n                    transaction.Commit();\n                }\n                catch (Exception ex)\n                {\n                    transaction.Rollback();\n                    Console.WriteLine("Error: " + ex.Message);\n                }\n            }\n        }\n    }\n\n    public static void Main()\n    {\n        string connectionString = "Data Source=YourDatabase.sdf;";\n        using (SqlCeConnection connection = new SqlCeConnection(connectionString))\n        {\n            connection.Open();\n\n            // Assume dataTable is populated with your data\n            DataTable dataTable = GetDataFromSomewhere();\n\n            InsertData(connection, dataTable);\n        }\n    }\n}