try {\n    // Code that might throw an IndexOutOfRangeException\n    int[] numbers = { 1, 2, 3 };\n    Console.WriteLine(numbers[5]); // This will throw an exception\n} catch (IndexOutOfRangeException ex) {\n    // Option 1: Throw a new exception and wrap the original one\n    throw new MyException("An error occurred while accessing the array.", ex);\n\n    // Option 2: Log and rethrow to preserve the stack trace\n    // Console.WriteLine("Logging details...");\n    // throw; // Uncomment this line to rethrow without resetting the stack trace\n\n    // Option 3: Rethrow with 'throw ex;' would reset the stack trace\n    // throw ex;\n}