using System;\nusing System.Xml.XPath;\n\n// Assuming you have the namespace URI from the XML\nstring namespaceUri = "http://some.namespace.uri"; // Replace with the actual namespace if any\n\nXPathDocument doc = new XPathDocument(HttpWebResponse.GetResponseStream());\nXPathNavigator nav = doc.CreateNavigator();\n\nXmlNamespaceManager nsManager = new XmlNamespaceManager(nav.NameTable);\nnsManager.AddNamespace("geo", namespaceUri); // 'geo' is a prefix, you can choose anything\n\n// Use the prefix in your XPath query if there's a namespace\nXPathNodeIterator nodes = nav.Select("//geo:places", nsManager);\n\nwhile (nodes.MoveNext())\n{\n    Console.WriteLine(nodes.Current.Name);\n}