using System;\nusing System.Data;\n\nclass Program\n{\n    static void Main()\n    {\n        // Assuming 'dataSet' is your existing DataSet which has already been filled.\n        DataSet dataSet = new DataSet();\n        PopulateDataSet(dataSet);  // Fill your dataset here.\n\n        DataTable dataTable = dataSet.Tables[0];  // Assuming you are working with the first table in the DataSet\n\n        // Remove unnecessary columns\n        dataTable.Columns.Remove("treatmentDate");\n\n        // Get distinct insurance company names\n        DataView view = new DataView(dataTable);\n        DataTable distinctValues = view.ToTable(true, "insurance comp. name");  // Pass true for distinct and column to select\n\n        // Display distinct insurance companies\n        Console.WriteLine("Distinct Insurance Companies:");\n        foreach (DataRow row in distinctValues.Rows)\n        {\n            Console.WriteLine(row["insurance comp. name"]);\n        }\n\n        // Sort by 'Name' before removing duplicates if needed\n        dataTable.DefaultView.Sort = "Name ASC";  // Sorting by Name\n\n        // Display sorted data based on name and retrieve only distinct rows\n        DataView sortedDistinctView = new DataView(dataTable);\n        DataTable finalTable = sortedDistinctView.ToTable(true, "Name", "insurance comp. name");\n\n        Console.WriteLine("\nSorted Distinct Rows:");\n        foreach (DataRow row in finalTable.Rows)\n        {\n            Console.WriteLine($"{row["Name"]} - {row["insurance comp. name"]}");\n        }\n    }\n\n    static void PopulateDataSet(DataSet dataSet)\n    {\n        // Here you would have your code to populate the DataSet from the database.\n        DataTable table = new DataTable();\n        table.Columns.Add("Name");\n        table.Columns.Add("insurance comp. name");\n        table.Columns.Add("treatmentDate");\n\n        // Sample data\n        table.Rows.Add("Ali Boz", "SGK", "12.04.09");\n        table.Rows.Add("Ali Boz", "SGK", "14.04.09");\n        table.Rows.Add("Veli AÅŸ", "AKBANK", "10.04.09");\n        // Add more rows as needed...\n\n        dataSet.Tables.Add(table);\n    }\n}