string connectionString = "Data Source=myOracleServer;User Id=myUsername;Password=myPassword;";\nusing (var conn = new OracleConnection(connectionString))\n{\n    conn.StateChange += (sender, args) =>\n    {\n        if (conn.State == ConnectionState.Open)\n        {\n            using (var cmd = conn.CreateCommand())\n            {\n                cmd.CommandText = "ALTER SESSION SET NLS_LANGUAGE=AMERICAN";\n                cmd.ExecuteNonQuery();\n                cmd.CommandText = "ALTER SESSION SET NLS_TERRITORY=AMERICA";\n                cmd.ExecuteNonQuery();\n                cmd.CommandText = "ALTER SESSION SET NLS_CHARACTERSET=WE8ISO8859P1";\n                cmd.ExecuteNonQuery();\n            }\n        }\n    };\n    conn.Open();\n}