string connectionString = $"Provider=Microsoft.Jet.OLEDB.4.0;Data Source={Server.MapPath(l_importFileName)};Extended Properties=\"Excel 8.0;HDR=YES\"";\nusing (OleDbConnection l_DbConnection = new OleDbConnection(connectionString))\n{\n    using (OleDbDataAdapter l_DbCommand = new OleDbDataAdapter("SELECT * FROM [Sheet1$]", l_DbConnection))\n    {\n        DataSet l_dataSet = new DataSet();\n        try\n        {\n            l_DbConnection.Open();\n            l_DbCommand.Fill(l_dataSet);\n        }\n        catch (Exception ex)\n        {\n            // Log exception details here\n        }\n    }\n}