using System;\nusing System.Diagnostics;\n\nclass Program\n{\n    static void Main()\n    {\n        // Start the first process with its own console\n        ProcessStartInfo startInfo1 = new ProcessStartInfo();\n        startInfo1.FileName = "cmd.exe";\n        startInfo1.Arguments = "/k \"echo Hello from Console 1\"";\n        Process.Start(startInfo1);\n\n        // Start the second process with its own console\n        ProcessStartInfo startInfo2 = new ProcessStartInfo();\n        startInfo2.FileName = "cmd.exe";\n        startInfo2.Arguments = "/k \"echo Hello from Console 2\"";\n        Process.Start(startInfo2);\n    }\n}