using System;\nusing System.Diagnostics;\nusing System.Windows.Forms;\n\npublic class MainForm : Form\n{\n    private Timer _timer;\n    private Label _statusLabel;\n\n    public MainForm()\n    {\n        // Initialize label\n        _statusLabel = new Label();\n        _statusLabel.Text = "Checking VPN status...";\n        _statusLabel.Location = new System.Drawing.Point(10, 10);\n        this.Controls.Add(_statusLabel);\n\n        // Initialize timer\n        _timer = new Timer();\n        _timer.Interval = 500; // Check every 500 ms\n        _timer.Tick += OnTimerTick;\n        _timer.Start();\n\n        this.Text = "VPN Status";\n        this.Size = new System.Drawing.Size(250, 100);\n    }\n\n    private void OnTimerTick(object sender, EventArgs e)\n    {\n        bool isVpnRunning = IsProcessRunning("openvpn"); // Change "openvpn" to your vpn process's name\n        _statusLabel.Text = isVpnRunning ? "VPN Connected" : "VPN Disconnected";\n        _timer.Stop(); // Stop after checking once. You can re-enable as needed.\n    }\n\n    private bool IsProcessRunning(string processName)\n    {\n        try\n        {\n            Process[] processes = Process.GetProcessesByName(processName);\n            return processes.Length > 0;\n        }\n        catch (Exception ex)\n        {\n            _statusLabel.Text = $"Error checking VPN status: {ex.Message}";\n            return false;\n        }\n    }\n\n    [STAThread]\n    static void Main()\n    {\n        Application.EnableVisualStyles();\n        Application.SetCompatibleTextRenderingDefault(false);\n        Application.Run(new MainForm());\n    }\n}