public List<string> GetUnretrievedMessageUIDs(Pop3Client client, HashSet<string> retrievedUids)\n{\n    List<string> newMessages = new List<string>();\n\n    // Connect and authenticate to the server\n    client.Connect("pop3.example.com", 110, false); // Adjust connection parameters as necessary\n    client.Authenticate("your_username", "your_password");\n\n    // Retrieve UIDLs from the server\n    var uids = client.GetMessageUids();\n\n    foreach (var uid in uids)\n    {\n        if (!retrievedUids.Contains(uid))\n        {\n            newMessages.Add(uid);\n            retrievedUids.Add(uid); // Store for future reference\n        }\n    }\n\n    return newMessages;\n}