using System.IO;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        string sourceBinaryFilePath = @"C:\windows\notepad.exe";\n        string largeBinaryFilePath = "large_natural_binary_file.bin";\n\n        int fileSizeInMegabytes = 2048; // Adjust size as needed.\n        int bytesToRead = (int)(fileSizeInMegabytes * 1024 * 1024);\n\n        using (FileStream sourceStream = new FileStream(sourceBinaryFilePath, FileMode.Open))\n        using (FileStream destinationStream = new FileStream(largeBinaryFilePath, FileMode.Create))\n        {\n            byte[] buffer = new byte[512 * 1024]; // 512 KB buffer.\n            int totalBytesRead = 0;\n\n            while (totalBytesRead < bytesToRead)\n            {\n                int bytesRead = sourceStream.Read(buffer, 0, Math.Min(bytesToRead - totalBytesRead, buffer.Length));\n                if (bytesRead == 0) // End of file reached, rewind and continue.\n                    sourceStream.Seek(0, SeekOrigin.Begin);\n\n                destinationStream.Write(buffer, 0, bytesRead);\n                totalBytesRead += bytesRead;\n            }\n        }\n    }\n}