using System;\nusing System.Collections.Generic;\nusing System.Linq;\n\nclass Program\n{\n    static void Main()\n    {\n        List<int> numbers = new List<int> { 1, 3, 2, 3, 4, 5, 3, 2, 1 };\n\n        int mostFrequentNumber = numbers.GroupBy(n => n)\n                                        .OrderByDescending(g => g.Count())\n                                        .First()\n                                        .Key;\n\n        Console.WriteLine("The most occurring number is: " + mostFrequentNumber);\n    }\n}