// Example of using SmtpClient and MailDefinition programmatically\nSystem.Net.Mail.SmtpClient client = new System.Net.Mail.SmtpClient("smtp.example.com");\nclient.Port = 587;\nclient.Credentials = new System.Net.NetworkCredential("username", "password");\nclient.EnableSsl = true;\n\nMailMessage mail = new MailMessage("from@example.com", "to@example.com", "Subject", "Body");\nclient.Send(mail);