public class FooData {\n    public double Price { get; set; }\n    public int Quantity { get; set; }\n}\n\npublic class FooWrapper {\n    private readonly FooData _fooData;\n\n    public FooWrapper(FooData fooData) => _fooData = fooData ?? throw new ArgumentNullException(nameof(fooData));\n\n    public double Price {\n        get => _fooData.Price;\n        set => _fooData.Price = value;\n    }\n\n    public int Quantity {\n        get => _fooData.Quantity;\n        set => _fooData.Quantity = value;\n    }\n\n    public double GetTotal() => Price * Quantity;\n}