using System;\nusing System.IO;\n\npublic class NetworkShareAccess\n{\n    public static bool CanReadDirectory(string sharePath)\n    {\n        try\n        {\n            // Try to enumerate files in the directory\n            var files = Directory.EnumerateFiles(sharePath).ToList();\n            return true; // If no exception, we have read access\n        }\n        catch (UnauthorizedAccessException)\n        {\n            // Access denied, user does not have read permissions\n            return false;\n        }\n        catch (DirectoryNotFoundException)\n        {\n            // The directory was not found, could be wrong path or other issues\n            return false;\n        }\n    }\n\n    public static void Main()\n    {\n        string sharePath = @"\\server\share$";\n        bool canRead = CanReadDirectory(sharePath);\n        Console.WriteLine(canRead ? "Read access granted" : "Read access denied");\n    }\n}