using System;\nusing System.Net;\nusing System.Net.Mail;\n\nclass Program\n{\n    static void Main()\n    {\n        // Create the mail message\n        MailMessage mail = new MailMessage("from@example.com", "to@example.com");\n        mail.Subject = "Email with PDF Attachment";\n        mail.Body = "Please find the attached document.";\n\n        // Specify the file to be attached\n        string pdfFilePath = @"C:\path\to\your\file.pdf";\n        \n        // Create a new attachment object and add it to the MailMessage\n        Attachment attachment = new Attachment(pdfFilePath);\n        mail.Attachments.Add(attachment);\n\n        // Set up the SMTP client settings\n        SmtpClient smtpClient = new SmtpClient("smtp.example.com");\n        smtpClient.Credentials = new NetworkCredential("username", "password");\n\n        try\n        {\n            // Send the email\n            smtpClient.Send(mail);\n            Console.WriteLine("Email sent successfully with attachment.");\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine($"Failed to send email. Error: {ex.Message}");\n        }\n\n        // Clean up resources\n        mail.Dispose();\n    }\n}