using System;\nusing System.Collections.Generic;\nusing System.Drawing;\n\npublic class FloodFiller\n{\n    public void FloodFill(Bitmap bitmap, int startX, int startY, Color targetColor)\n    {\n        Stack<Point> pixelStack = new Stack<Point>();\n        Color startColor = bitmap.GetPixel(startX, startY);\n\n        // If the start color is the same as the target color, do nothing.\n        if (startColor == targetColor) return;\n\n        pixelStack.Push(new Point(startX, startY));\n\n        while (pixelStack.Count > 0)\n        {\n            Point currentPoint = pixelStack.Pop();\n            int x = currentPoint.X;\n            int y = currentPoint.Y;\n\n            // Check for bounds and color match\n            if (x < 0 || x >= bitmap.Width || y < 0 || y >= bitmap.Height) continue;\n            if (!Equals(bitmap.GetPixel(x, y), startColor)) continue;\n\n            bitmap.SetPixel(x, y, targetColor);\n\n            pixelStack.Push(new Point(x + 1, y));\n            pixelStack.Push(new Point(x - 1, y));\n            pixelStack.Push(new Point(x, y + 1));\n            pixelStack.Push(new Point(x, y - 1));\n        }\n    }\n\n    private bool Equals(Color colorA, Color colorB)\n    {\n        return colorA.R == colorB.R && colorA.G == colorB.G && colorA.B == colorB.B;\n    }\n}