var groupedByHour = from record in dbContext.YourTable\n                    group record by new \n                    { \n                        Year = record.YourDateTimeColumn.Year,\n                        Month = record.YourDateTimeColumn.Month,\n                        Day = record.YourDateTimeColumn.Day,\n                        Hour = record.YourDateTimeColumn.Hour\n                    } into hoursGroup\n                    select new \n                    {\n                        DateHour = new DateTime(hoursGroup.Key.Year, hoursGroup.Key.Month, hoursGroup.Key.Day, hoursGroup.Key.Hour, 0, 0),\n                        Count = hoursGroup.Count()\n                    };