using System;\nusing System.Collections.Generic;\nusing System.Data.SqlClient;\n\nclass Program\n{\n    static void Main()\n    {\n        string connectionString = "Server=your_server;Database=your_database;User Id=your_user;Password=your_password;";\n        \n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            connection.Open();\n\n            // Get List of Tables\n            string queryTables = "SELECT TABLE_NAME FROM INFORMATION_SCHEMA.TABLES WHERE TABLE_TYPE='BASE TABLE'";\n            SqlCommand commandTables = new SqlCommand(queryTables, connection);\n            SqlDataReader readerTables = commandTables.ExecuteReader();\n            \n            var tables = new List<string>();\n            while (readerTables.Read())\n            {\n                tables.Add(readerTables["TABLE_NAME"].ToString());\n            }\n            readerTables.Close();\n\n            foreach (string table in tables)\n            {\n                Console.WriteLine($"Table: {table}");\n                \n                // Get List of Columns for each Table\n                string queryColumns = $"SELECT COLUMN_NAME, DATA_TYPE FROM INFORMATION_SCHEMA.COLUMNS WHERE TABLE_NAME='{table}'";\n                SqlCommand commandColumns = new SqlCommand(queryColumns, connection);\n                SqlDataReader readerColumns = commandColumns.ExecuteReader();\n\n                while (readerColumns.Read())\n                {\n                    Console.WriteLine($"  Column: {readerColumns["COLUMN_NAME"]}, Type: {readerColumns["DATA_TYPE"]}");\n                }\n                readerColumns.Close();\n            }\n        }\n    }\n}