using System;\nusing System.Collections.Generic;\nusing System.Diagnostics;\n\nclass Program\n{\n    static void Main()\n    {\n        // Get all processes currently running on the computer\n        Process[] processes = Process.GetProcesses();\n\n        foreach (Process process in processes)\n        {\n            try\n            {\n                Console.WriteLine($"Name: {process.ProcessName}");\n                Console.WriteLine($"ID: {process.Id}");\n\n                if (!process.HasExited)\n                {\n                    // Total private memory size\n                    Console.WriteLine($"Private Memory Size: {process.WorkingSet64} bytes");\n\n                    // Total CPU time spent on this process\n                    Console.WriteLine($"Total Processor Time: {process.TotalProcessorTime}");\n                }\n                else\n                {\n                    Console.WriteLine("The process has exited.");\n                }\n\n                Console.WriteLine();\n            }\n            catch (Exception ex)\n            {\n                // Handle possible exceptions here, e.g., permission issues accessing some processes\n                Console.WriteLine($"Failed to get data for process ID {process.Id}: {ex.Message}");\n            }\n        }\n    }\n}