using System;\nusing System.Configuration;\n\nclass Program\n{\n    static void Main()\n    {\n        Configuration config = ConfigurationManager.OpenExeConfiguration(ConfigurationUserLevel.None);\n\n        // Assuming there's a <location> with path="/SomePath"\n        ConfigurationLocationCollection locations = config.Locations;\n        \n        foreach (ConfigurationLocation location in locations)\n        {\n            if (location.Path.Equals("/SomePath", StringComparison.OrdinalIgnoreCase))\n            {\n                // Access child sections within this location\n                ConnectionStringsSection connectionStringSection = location.Configuration.ConnectionStrings;\n\n                foreach (ConnectionStringSettings setting in connectionStringSection.ConnectionStrings)\n                {\n                    Console.WriteLine($"Connection String Name: {setting.Name}");\n                    Console.WriteLine($"Provider: {setting.ProviderName}");\n                    Console.WriteLine($"Value: {setting.ConnectionString}");\n                }\n            }\n        }\n    }\n}