using Microsoft.Office.Interop.Excel;\nusing System.Runtime.InteropServices;\n\nclass Program\n{\n    static void Main()\n    {\n        Application excel = new Application();\n        Workbook workbook = excel.Workbooks.Add();\n        Worksheet worksheet = workbook.Sheets[1];\n\n        // Path to image file\n        string imagePath = @"C:\path\to\image.jpg";\n        \n        // Position the image in a cell (B2)\n        double leftPosition = ((Range)worksheet.Cells[2, 2]).Left;\n        double topPosition = ((Range)worksheet.Cells[2, 2]).Top;\n\n        worksheet.Shapes.AddPicture(imagePath, MsoTriState.msoFalse, MsoTriState.msoCTrue, leftPosition, topPosition);\n\n        excel.Visible = true;\n    }\n}