protected void BenefitListView_ItemDataBound(object sender, ListViewItemEventArgs e)\n{\n    if (Request.QueryString["id"] == "no")\n    {\n        // Find the table within the ListView's LayoutTemplate\n        HtmlTable tblBenefits = (HtmlTable)e.Item.FindControl("tblBenefits");\n        TableRow headerRow = null;\n\n        // Assuming the first row is the header row\n        if (tblBenefits != null && tblBenefits.Rows.Count > 0)\n        {\n            headerRow = tblBenefits.Rows[0];\n        }\n\n        // Hide the ID column in both header and data rows\n        if (headerRow != null)\n        {\n            headerRow.Cells[0].Visible = false;\n        }\n\n        if (e.Item.ItemType == ListViewItemType.DataItem)\n        {\n            HtmlTableCell tdID = (HtmlTableCell)e.Item.FindControl("tdID");\n            if (tdID != null)\n            {\n                tdID.Visible = false;\n            }\n        }\n    }\n}