using Renci.SshNet;\nusing System.IO;\n\npublic void UploadFile(string host, string username, string password, string localFilePath, string remotePath)\n{\n    using (var sftp = new SftpClient(host, 22, username, password))\n    {\n        sftp.Connect();\n        var fileStream = File.OpenRead(localFilePath);\n        sftp.UploadFile(fileStream, remotePath, true); // true to overwrite if the file already exists\n        fileStream.Close();\n        sftp.Disconnect();\n    }\n}\n\npublic void DownloadFile(string host, string username, string password, string remoteFilePath, string localPath)\n{\n    using (var sftp = new SftpClient(host, 22, username, password))\n    {\n        sftp.Connect();\n        var fileStream = File.OpenWrite(localPath);\n        sftp.DownloadFile(remoteFilePath, fileStream);\n        fileStream.Close();\n        sftp.Disconnect();\n    }\n}