protected void GridView1_RowDataBound(object sender, GridViewRowEventArgs e)\n{\n    if (e.Row.RowType == DataControlRowType.DataRow)\n    {\n        HyperLink hyperlink = new HyperLink();\n        hyperlink.NavigateUrl = "YourURL.aspx?id=" + e.Row.Cells[0].Text; // Example URL and parameter\n        hyperlink.Text = e.Row.Cells[1].Text; // Text for the hyperlink\n        e.Row.Cells[2].Controls.Add(hyperlink); // Add to a specific column cell\n    }\n}