using System;\nusing System.Data;\nusing Oracle.ManagedDataAccess.Client;\n\nclass Program\n{\n    static void Main()\n    {\n        string connectionString = "User Id=<username>;Password=<password>;Data Source=<datasource>";\n        \n        try\n        {\n            using (OracleConnection connection = new OracleConnection(connectionString))\n            {\n                connection.Open();\n                Console.WriteLine("Successfully connected to Oracle Server!");\n            }\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("Failed to connect to Oracle Server. Error: " + ex.Message);\n        }\n    }\n}