using System;\nusing System.IO;\nusing System.Linq;\nusing System.Xml.Linq;\n\nclass Program\n{\n    static void Main()\n    {\n        // Define the path to your log file\n        string logFilePath = "path/to/your/logfile.log";\n\n        // Read all lines from the log file\n        var xmlMessages = File.ReadAllLines(logFilePath);\n\n        // Parse each line as an XML document and filter based on criteria\n        foreach (var line in xmlMessages)\n        {\n            try\n            {\n                XDocument doc = XDocument.Parse(line);\n                \n                // Define your search parameters here, for example:\n                // Searching for a node named 'SpecificNode' with specific value 'desiredValue'\n                if (doc.Descendants("SpecificNode").Any(e => e.Value == "desiredValue"))\n                {\n                    Console.WriteLine(doc.ToString());\n                }\n            }\n            catch (Exception ex)\n            {\n                // Handle invalid XML lines\n                Console.Error.WriteLine($"Failed to parse line: {line}");\n            }\n        }\n    }\n}