using Quartz;\nusing Quartz.Impl;\n\npublic class Program\n{\n    public static void Main(string[] args)\n    {\n        IScheduler scheduler = StdSchedulerFactory.GetDefaultScheduler().Result;\n        scheduler.Start();\n\n        IJobDetail job = JobBuilder.Create<SmsSendJob>()\n            .WithIdentity("smsJob", "group1")\n            .Build();\n\n        ITrigger trigger = TriggerBuilder.Create()\n            .WithIdentity("simpleTrigger", "group1")\n            .StartNow()\n            .WithSimpleSchedule(x => x\n                .WithIntervalInSeconds(30) // or your timing preference \n                .RepeatForever())\n            .Build();\n\n        scheduler.ScheduleJob(job, trigger);\n\n        Console.WriteLine("Press any key to close the application");\n        Console.ReadKey();\n    }\n}\n\npublic class SmsSendJob : IJob\n{\n    public Task Execute(IJobExecutionContext context)\n    {\n        SmsSender smsSender = new SmsSender();\n        smsSender.SendScheduledSms("+15558675309", "Hello from scheduled task!");\n        return Task.CompletedTask;\n    }\n}