[AspNetCompatibilityRequirements(RequirementsMode = AspNetCompatibilityRequirementsMode.Allowed)]\n[BehaviorConfiguration = "myBehavior"]\npublic class MyService : IMyService\n{\n    static Dictionary<string, List<string>> sessionList = new Dictionary<string, List<string>>();\n\n    public void StoreData(string data)\n    {\n        Guid sessionId = OperationContext.Current.SessionId;\n        if (!sessionList.ContainsKey(sessionId.ToString()))\n        {\n            sessionList[sessionId.ToString()] = new List<string>();\n        }\n        sessionList[sessionId.ToString()].Add(data);\n    }\n\n    public string RetrieveData()\n    {\n        Guid sessionId = OperationContext.Current.SessionId;\n        return sessionList.ContainsKey(sessionId.ToString()) ? String.Join(", ", sessionList[sessionId.ToString()]) : "No Data";\n    }\n}