using System;\nusing System.Linq;\nusing System.Xml.Linq;\n\nclass Program\n{\n    static void Main()\n    {\n        // Load your XML document\n        XDocument xdoc = XDocument.Load("yourfile.xml");\n\n        // Define the namespace using a local alias since the URI is not available or needed for parsing.\n        XNamespace ns = "http://www.naturalconvergence.com/schema/cdr/v3/cdr";\n\n        // Querying the XML, assuming you want to get call_id of each cdr\n        var callIds = xdoc.Descendants(ns + "cdr")\n            .Select(x => (string)x.Element(ns + "call_id"))\n            .ToList();\n\n        // Output the results\n        foreach (var id in callIds)\n        {\n            Console.WriteLine("Call ID: " + id);\n        }\n    }\n}