string BuildingFile = Server.MapPath("buildings.txt");\nStreamReader FileStreamReader = File.OpenText(BuildingFile);\n\nwhile (FileStreamReader.Peek() != -1)\n{\n    string[] words = FileStreamReader.ReadLine().Split(',');\n\n    // Ensure the line has at least 3 columns before processing\n    if (words.Length >= 3)\n    {\n        string building = words[0].Trim();\n        string person = words[1].Trim();\n        string duty = words[2].Trim();\n\n        switch (building)\n        {\n            case "bldgA":\n                Console.WriteLine($"You are in {building} and your name is {person} and your duty is {duty}");\n                break;\n            case "bldgB":\n                Console.WriteLine($"You are in {building} and your name is {person} and your duty is {duty}");\n                // Add more cases as needed\n                break;\n        }\n    }\n}\n\nFileStreamReader.Close();