using System;\nusing System.Data;\nusing System.Data.SqlClient;\nusing System.Windows;\n\nnamespace WpfAppExample\n{\n    public partial class MainWindow : Window\n    {\n        private SqlDataAdapter da;\n        private DataTable dt;\n\n        public MainWindow()\n        {\n            InitializeComponent();\n            \n            // Initialize connection and data adapter\n            using (SqlConnection cn = new SqlConnection("your_connection_string_here"))\n            {\n                string selectCommand = "SELECT * FROM YourTable";\n                da = new SqlDataAdapter(selectCommand, cn);\n\n                SqlCommandBuilder cb = new SqlCommandBuilder(da); // Creates necessary commands\n\n                dt = new DataTable();\n                da.Fill(dt); // Fills the data table with initial data\n\n                DataGrid.ItemsSource = dt.DefaultView;\n            }\n        }\n\n        private void SaveButton_Click(object sender, RoutedEventArgs e)\n        {\n            try\n            {\n                int rowsUpdated = da.Update(dt);\n                MessageBox.Show($"Rows updated: {rowsUpdated}");\n            }\n            catch (Exception ex)\n            {\n                MessageBox.Show("An error occurred while updating data: " + ex.Message);\n            }\n        }\n    }\n}