using NLog;\n\nclass Program\n{\n    private static readonly Logger logger = LogManager.GetCurrentClassLogger();\n\n    static void Main()\n    {\n        // Configure NLog to use multiple loggers\n        var config = new NLog.Config.LoggingConfiguration();\n        \n        // Console target 1\n        var consoleTarget1 = new NLog.Targets.ConsoleTarget("console1");\n        config.AddRule(LogLevel.Info, LogLevel.Fatal, consoleTarget1);\n\n        // Console target 2\n        var consoleTarget2 = new NLog.Targets.ConsoleTarget("console2");\n        config.AddRule(LogLevel.Debug, LogLevel.Fatal, consoleTarget2);\n\n        LogManager.Configuration = config;\n\n        logger.Info("Message for Console 1 - Info level");\n        logger.Debug("Message for Console 2 - Debug level");\n    }\n}