using Microsoft.Win32;\nusing System;\n\npublic class ConfigReader\n{\n    private static string _dbConnectionString;\n    \n    public static string GetDbConnectionString()\n    {\n        if (_dbConnectionString == null)\n        {\n            // Accessing registry once and caching the value\n            using (var key = Registry.LocalMachine.OpenSubKey(@"SOFTWARE\YourAppConfig"))\n            {\n                _dbConnectionString = key?.GetValue("ConnectionString") as string;\n            }\n        }\n\n        return _dbConnectionString;\n    }\n}