using (var conn = new SqlConnection(connectionString))\n{\n    conn.Open();\n    using (SqlCommand cmd = new SqlCommand("InsertContacts", conn))\n    {\n        cmd.CommandType = CommandType.StoredProcedure;\n\n        // Create a DataTable with the correct structure\n        var dataTable = new DataTable();\n        dataTable.Columns.Add("ContactName");\n        dataTable.Columns.Add("Designation");\n\n        // Add your rows to the DataTable\n        dataTable.Rows.Add("A", "dummy");\n        dataTable.Rows.Add("B", "sam");\n\n        // Convert the DataTable to a SqlParameter for TVP\n        var tvpParam = new SqlParameter("@ContactDetails", SqlDbType.Structured);\n        tvpParam.TypeName = "dbo.ContactDetails";\n        tvpParam.Value = dataTable;\n\n        cmd.Parameters.Add(tvpParam);\n\n        cmd.ExecuteNonQuery();\n    }\n}